/**
 * @author wx: shuidi_tech
 */
// 在这里，您可以通过 ‘params’  获取节点中的输入变量，并通过 'ret' 输出结果
// 'params' 和 'ret' 已经被正确地注入到环境中
// 下面是一个示例，获取节点输入中参数名为‘input’的值：
// const input = params.input;
// 下面是一个示例，输出一个包含多种数据类型的 'ret' 对象：
// const ret = { "name": ‘小明’, "hobbies": [“看书”, “旅游”] };

async function main({ params }: Args): Promise<Output> {
    return {
        records: [
            {
                fields: JSON.stringify({
                    链接: params.link,
                    标题: params.title,
                    封面: params.cover,
                    内容: params.content,
                    开头钩子: params.opening_text,
                    连接词: params.transition_text,
                    结尾钩子: params.ending_text
                }),
            },
        ],
    };
}

// 在这里，您可以通过 ‘params’  获取节点中的输入变量，并通过 'ret' 输出结果
// 'params' 和 'ret' 已经被正确地注入到环境中
// 下面是一个示例，获取节点输入中参数名为‘input’的值：
// const input = params.input; 
// 下面是一个示例，输出一个包含多种数据类型的 'ret' 对象：
// const ret = { "name": ‘小明’, "hobbies": [“看书”, “旅游”] };

async function main({ params }: Args): Promise<Output> {
    let transitionTextList: {
        short_sentence?: string;
        example?: string;
    }[] = [];

    try {
        transitionTextList = JSON.parse(params.input)
    } catch {
        transitionTextList = []
    }

    return {
        output: transitionTextList?.map((item, index) => `${index + 1}. 短语: ${item.short_sentence}（示例: ${item.example}）`).join('\n') ?? ''
    }
}
