// 定义类型
interface TimelineSegment {
    start: number;
    end: number;
}

interface Args {
    params: {
        all_timelines: TimelineSegment[];
        all_timelines_1: TimelineSegment[];
        audio_urls: string[]; // 音频链接列表
    };
}

interface Output {
    audio_timeline: TimelineSegment[];
    merged_audio_urls: string[]; // 修改为字符串数组
}

// 主函数 - 生成音频时间线
async function main({ params }: Args): Promise<Output> {
    // 获取视频总时长
    const videoDuration = params.all_timelines[0].end;

    // 获取音频总时长
    const audioDuration = params.all_timelines_1[0].end;

    // 计算需要循环的次数（向上取整）
    const loopCount = Math.ceil(videoDuration / audioDuration);

    // 存储生成的音频时间线
    const audioTimeline: TimelineSegment[] = [];

    // 存储合并后的音频链接数组
    const mergedAudioUrls: string[] = [];

    // 当前时间指针
    let currentTime = 0;

    // 循环生成音频时间线
    for (let i = 0; i < loopCount; i++) {
        // 计算当前片段的结束时间
        const segmentEnd = Math.min(currentTime + audioDuration, videoDuration);

        // 添加到时间线
        audioTimeline.push({
            start: currentTime,
            end: segmentEnd
        });

        // 复制音频链接到数组
        if (params.audio_urls && params.audio_urls.length > 0) {
            // 将当前循环的音频链接添加到结果数组
            mergedAudioUrls.push(...params.audio_urls);
        }

        // 更新当前时间指针
        currentTime = segmentEnd;

        // 如果已达到视频结尾，结束循环
        if (currentTime >= videoDuration) {
            break;
        }
    }

    return {
        audio_timeline: audioTimeline,
        merged_audio_urls: mergedAudioUrls // 字符串数组
    };
}
