async function main({ params }: Args): Promise<Output> {

    let base_duration = params.base_duration
    let related_duration = params.related_duration
    params.related_mp3[0].data.link
    params.base_mp3
    params.base_word.word
    params.related_words[0].word


    let mp3_infos = []
    let sub_time = 2
    let all_time = base_duration + sub_time
    mp3_infos.push({
        "audio_url": params.base_mp3,
        "start": 0,
        "end": base_duration*1000000
    })

        //短字幕
    let base11 = [{"text": params.base_word.word ,"start":0,"end": base_duration * 1000000,"keyword": params.base_word.word, "font_size":10, "keyword_font_size":10 }]
    let word11 = []
    let word22 = []
    let word33 = []
    let word44 = []
    let word55 = []
    let word66 = []

    let bg_infos = [
        {
            "image_url": params.img_url,
            "width": 576,
            "height": 1024,
            "start": 0,
            "end": (base_duration +sub_time) *1000000 | 0,
            "transition": "水墨",  //对应剪映的转场动画名字   可选
            "transition_duration": 500000 //对应剪映的转场动画时长 ，范围是500000-2500000  可选

        }
    ]

    for (let index = 0; index < related_duration.length; index++) {
        const element = related_duration[index];
        let start = all_time*1000000 | 0 
        let end = start + element.duration*1000000 | 0
        mp3_infos.push({
            "audio_url": params.related_mp3[index].data.link,
            "start": start,
            "end": end
        })

        bg_infos.push({
            "image_url": params.word_imgs[index],
            "width": 576,
            "height": 1024,
            "start": start,
            "end": end + sub_time * 1000000,
            "transition": "水墨",  //对应剪映的转场动画名字   可选
            "transition_duration": 500000 //对应剪映的转场动画时长 ，范围是500000-2500000  可选
        })

        switch(index) {
            case 0:
                word11.push({
                    "text": params.related_words[index].word ,
                    "start": start,
                    "end": end,
                    "keyword": params.base_word.word ,
                    "font_size":10,
                    "keyword_font_size":10
                })
                break;
            case 1:
                word22.push({
                    "text": params.related_words[index].word,
                    "start": start,
                    "end": end,
                    "keyword": params.base_word.word,
                    "font_size":10,
                    "keyword_font_size":10
                })
                break;
            case 2:
                word33.push({
                    "text": params.related_words[index].word,
                    "start": start,
                    "end": end,
                    "keyword": params.base_word.word,
                    "font_size":10,
                    "keyword_font_size":10
                })
                break;
            case 3:
                word44.push({
                    "text": params.related_words[index].word,
                    "start": start,
                    "end": end,
                    "keyword": params.base_word.word,
                    "font_size":10,
                    "keyword_font_size":10
                })
                break;
            case 4:
                word55.push({
                    "text": params.related_words[index].word,
                    "start": start,
                    "end": end,
                    "keyword": params.base_word.word,
                    "font_size":10,
                    "keyword_font_size":10
                })
                break;
            case 5:
                word66.push({
                    "text": params.related_words[index].word,
                    "start": start,
                    "end": end,
                    "keyword": params.base_word.word,
                    "font_size":10,
                    "keyword_font_size":10
                })
                break;
            default:
                break;
        }


        all_time =  all_time + element.duration +sub_time

    }




    let mask_infos = [{
        "image_url": params.bg_url,
        "width": 576,
        "height": 1024,
        "start": 0,
        "end": all_time*1000000 | 0
    }]

    //长字幕
    let base1 = [{"text": params.base_word.word + "\n" + params.base_word.phonetic+"\n"+params.base_word.definition,"start":0,"end": all_time*1000000 | 0,"keyword": params.base_word.word+"|"+ params.base_word.phonetic , "font_size":10, "keyword_font_size":10 }]
    let word1 = [{"text": params.related_words[0].word + "\n" + params.related_words[0].phonetic + "\n" + params.related_words[0].definition, "start": 0, "end": all_time * 1000000 | 0, "keyword": params.base_word.word + "|" + params.related_words[0].phonetic , "font_size":10 , "keyword_font_size":10}];
    let word2 = [{"text": params.related_words[1].word + "\n" + params.related_words[1].phonetic + "\n" + params.related_words[1].definition, "start": 0, "end": all_time * 1000000 | 0, "keyword": params.base_word.word + "|" + params.related_words[1].phonetic , "font_size":10 , "keyword_font_size":10}]; 
    let word3 = [{"text": params.related_words[2].word + "\n" + params.related_words[2].phonetic + "\n" + params.related_words[2].definition, "start": 0, "end": all_time * 1000000  | 0, "keyword": params.base_word.word + "|" + params.related_words[2].phonetic , "font_size":10, "keyword_font_size":10}];
    let word4 = [{"text": params.related_words[3].word + "\n" + params.related_words[3].phonetic + "\n" + params.related_words[3].definition, "start": 0, "end": all_time * 1000000 | 0, "keyword": params.base_word.word + "|" + params.related_words[3].phonetic , "font_size":10, "keyword_font_size":10}];
    let word5 = [{"text": params.related_words[4].word + "\n" + params.related_words[4].phonetic + "\n" + params.related_words[4].definition, "start": 0, "end": all_time * 1000000 | 0, "keyword": params.base_word.word + "|" + params.related_words[4].phonetic, "font_size":10, "keyword_font_size":10}];
    let word6 = [{"text": params.related_words[5].word + "\n" + params.related_words[5].phonetic + "\n" + params.related_words[5].definition, "start": 0, "end": all_time * 1000000 | 0, "keyword": params.base_word.word + "|" + params.related_words[5].phonetic, "font_size":10, "keyword_font_size":10}];


    // 构建输出对象
    const ret = {
        "bg_infos": JSON.stringify(bg_infos),
        "mask_infos":JSON.stringify(mask_infos),
        "mp3_infos":JSON.stringify(mp3_infos),
        "base_infos":JSON.stringify(base1),
        "word1":JSON.stringify(word1),
        "word2":JSON.stringify(word2),
        "word3":JSON.stringify(word3),
        "word4":JSON.stringify(word4),
        "word5":JSON.stringify(word5),
        "word6":JSON.stringify(word6),
        "base11":JSON.stringify(base11),
        "word11":JSON.stringify(word11),
        "word22":JSON.stringify(word22),
        "word33":JSON.stringify(word33),
        "word44":JSON.stringify(word44),
        "word55":JSON.stringify(word55),
        "word66":JSON.stringify(word66),

    };

    return ret;
}

async function main({ params }: Args): Promise<Output> {

    let keys = []
    for(let info of params.infos){
        let duration =  info.end - info.start 
        keys.push({"offset":0,"property":"UNIFORM_SCALE","segment_id":info.id,"value":1})
        keys.push({"offset":duration/2|0,"property":"UNIFORM_SCALE","segment_id":info.id,"value":2})
        keys.push({"offset":duration,"property":"UNIFORM_SCALE","segment_id":info.id,"value":1})
    }


    return {"keys":JSON.stringify(keys)};
}

