async function main({ params }: Args): Promise<Output> {

    let key = []
    for (let info of params.input) {
        key.push({
            "offset": 0,
            "property": "KFTypePositionY",
            "segment_id": info.id,
            "value": 0.8
        })

        key.push({
            "offset": info.end - info.start,
            "property": "KFTypePositionY",
            "segment_id": info.id,
            "value": -0.8
        })

    }

    // 构建输出对象
    const ret = {
        "key0": JSON.stringify(key)
    }

    return ret;
}

