// const ret = { "name": ‘小明’, "hobbies": [“看书”, “旅游”] };

async function main({ params }: Args): Promise<Output> {
    let list: {
        title?: string;
        content?: string;
    }[] = [];

    try {
        list = JSON.parse(params.input)
    } catch {
        list = [];
    }

    return {
        new_content_1: list?.[0]?.content ?? `生成失败；输出原文: ${params.input}`,
        new_content_2: list?.[1]?.content ?? `生成失败；输出原文: ${params.input}`,
        new_content_3: list?.[2]?.content ?? `生成失败；输出原文: ${params.input}`,
        new_title_1: list?.[0]?.title ?? `生成失败；输出原文: ${params.input}`,
        new_title_2: list?.[1]?.title ?? `生成失败；输出原文: ${params.input}`,
        new_title_3: list?.[2]?.title ?? `生成失败；输出原文: ${params.input}`,
    };
}
