async function main({ params }: Args): Promise<Output> {
    const {texts,duration_list,audio_links}=params;

    let max_duration = 0;
    let start_time = 0;
    const audio_data = [];
    const audio_timelines =[];
    const common_timelines =[];
    const text_timelines =[];

    for (let i = 0; i < audio_links.length && i < duration_list.length; i++) {
        const duration = duration_list[i];
        audio_data.push({
            audio_url: audio_links[i],
            duration,
            start: start_time,
            end: start_time + duration,
            volume: 3
        });
        audio_timelines.push({
            start: start_time,
            end: start_time + duration
        });

        text_timelines.push({
            start: start_time,
            end: start_time + duration
        })

        start_time += duration;
        max_duration = start_time;
    }

    common_timelines.push({
        start: 0,
        end: max_duration
    });

    // æ„å»ºè¾“å‡ºå¯¹è±¡
    const ret = {
        audio_data: JSON.stringify(audio_data), 
        audio_timelines: audio_timelines,
        common_timelines: common_timelines,
        texts: texts,
        text_timelines: text_timelines
    };

    return ret;
}

