function splitTextsByType(struct_data, texts, text_timelines) {
    // åˆå§‹åŒ–ç»“æœç»“æ„
    const result = {
        first_text_part: { texts: [], timelines: [] },
        key_text_part: { texts: [], timelines: [] },
        merged_image_parts: { 
            texts: [],        // åˆå¹¶åçš„æ–‡æ¡ˆ
            timelines: [],    // åˆå¹¶åçš„æ—¶é—´çº¿
            ori_texts: [],    // åŸå§‹éå…³é”®æ–‡æœ¬é›†åˆï¼ˆæœªåˆå¹¶ï¼‰
            ori_timelines: [] // åŸå§‹éå…³é”®æ–‡æœ¬æ—¶é—´çº¿
        }
    };

    if (texts.length === 0) return result;

    let firstIndex = -1;
    let keyIndex = -1;

    // æŸ¥æ‰¾å…³é”®æ–‡æœ¬ä½ç½®
    if (struct_data.first_text) {
        firstIndex = texts.findIndex(text => text.includes(struct_data.first_text));
    }
    if (struct_data.key_text) {
        keyIndex = texts.findIndex(text => text.includes(struct_data.key_text));
        // ç¡®ä¿å…³é”®æ–‡æœ¬åœ¨é¦–æ–‡æœ¬ä¹‹å
        if (firstIndex !== -1 && keyIndex <= firstIndex) keyIndex = -1;
    }

    // å¤„ç†è¿ç»­çš„éæ–‡æ¡ˆéƒ¨åˆ†ï¼ˆæŒ‰3å¥åˆ†ç»„ï¼‰
    const processImagePart = (start, end) => {
        if (start >= end) return;

        const partTexts = texts.slice(start, end);
        const partTimelines = text_timelines.slice(start, end);

        // æ·»åŠ åˆ°åŸå§‹éå…³é”®æ–‡æœ¬é›†åˆ
        result.merged_image_parts.ori_texts.push(...partTexts);
        result.merged_image_parts.ori_timelines.push(...partTimelines);

        // æŒ‰æ¯3å¥åˆ†ç»„å¤„ç†
        for (let i = 0; i < partTexts.length; i += 1) {
            const chunk = partTexts.slice(i, i + 1);
            const timelineChunk = partTimelines.slice(i, i + 1);

            // åˆå¹¶æ–‡æ¡ˆï¼ˆç©ºæ ¼è¿æ¥ï¼‰
            const mergedText = chunk.join(' ');

            // åˆå¹¶æ—¶é—´çº¿ï¼šå–ç»„å†…ç¬¬ä¸€æ¡çš„å¼€å§‹æ—¶é—´å’Œæœ€åä¸€æ¡çš„ç»“æŸæ—¶é—´
            const mergedTimeline = {
                start: timelineChunk[0].start,
                end: timelineChunk[timelineChunk.length - 1].end
            };

            result.merged_image_parts.texts.push(mergedText);
            result.merged_image_parts.timelines.push(mergedTimeline);
        }
    };

    // æ ¹æ®å…³é”®æ–‡æœ¬ä½ç½®è¿›è¡Œåˆ†æ®µå¤„ç†
    if (firstIndex !== -1 && keyIndex !== -1) {
        // æ·»åŠ é¦–æ–‡æœ¬
        result.first_text_part.texts.push(texts[firstIndex]);
        result.first_text_part.timelines.push(text_timelines[firstIndex]);

        // æ·»åŠ å…³é”®æ–‡æœ¬
        result.key_text_part.texts.push(texts[keyIndex]);
        result.key_text_part.timelines.push(text_timelines[keyIndex]);

        // å¤„ç†ä¸‰ä¸ªå¯èƒ½çš„éæ–‡æ¡ˆè¿ç»­éƒ¨åˆ†
        processImagePart(0, firstIndex);
        processImagePart(firstIndex + 1, keyIndex);
        processImagePart(keyIndex + 1, texts.length);
    } 
    else if (firstIndex !== -1) {
        result.first_text_part.texts.push(texts[firstIndex]);
        result.first_text_part.timelines.push(text_timelines[firstIndex]);

        processImagePart(0, firstIndex);
        processImagePart(firstIndex + 1, texts.length);
    } 
    else if (keyIndex !== -1) {
        result.key_text_part.texts.push(texts[keyIndex]);
        result.key_text_part.timelines.push(text_timelines[keyIndex]);

        processImagePart(0, keyIndex);
        processImagePart(keyIndex + 1, texts.length);
    } 
    else {
        // æ•´ä¸ªä½œä¸ºéæ–‡æ¡ˆéƒ¨åˆ†å¤„ç†
        processImagePart(0, texts.length);
    }

    return result;
}
