async function main({ params }: Args): Promise<Output> { 
    let audios = [];
    let images = [];
    let images2 = [];
    let texts = [];
    let start = 0;

    // Calculate the total duration of all audio tracks and ensure it's rounded to four digits after the decimal point
    const totalDuration = Math.round(params.mp3_druations.reduce((sum, duration) => sum + (duration || 0), 0) * 1000000 / 10000) * 10000;

    // Process audio and text data
    for (let index = 0; index < params.mp3_urls.length; index++) {
        const duration = Math.round((params.mp3_druations[index] || 0) * 1000000 / 10000) * 10000;
        const end = start + duration;

        audios.push({
            "audio_url": params.mp3_urls[index],
            "duration": duration,
            "volume": 10,
            "start": start,
            "end": end
        });

        texts.push({
            "text": params.texts[index] || '',
            "start": start,
            "end": end,
            "in_animation": '',
            "out_animation": ''
        });

        start = end;
    }

    // Process image data (images array)
    let image_start = 0;
    for (let index = 0; index < params.images.length; index++) {
        const duration = Math.round((params.mp3_druations[index] || 0) * 1000000 / 10000) * 10000;
        const end = image_start + duration;

        images.push({
            "image_url": params.images[index],
            "width": 1920,
            "height": 1080,
            "start": image_start,
            "end": end,
            "in_animation": "æ”¾å¤§",
            "out_animation": "",
            "loop_animation": "",
            "in_animation_duration": 300000,
            "out_animation_duration": 300000
        });

        image_start = end;
    }

    // Process images2 (single image)
    if (params.images2) {
        images2.push({
            "image_url": params.images2,
            "width": 1920,
            "height": 1080,
            "start": 0,
            "end": totalDuration,
            "in_animation": "",
            "out_animation": "",
            "loop_animation": "",
            "in_animation_duration": 20000,
            "out_animation_duration": 20000
        });
    }

    // Effect information (use total start time)
    let effect_infos = [{
        "effect_title": "é‡‘ç²‰é—ªé—ª",
        "start": 0,
        "end": start
    }];

    // Background music information
    let bg_mp3 = [{
        "audio_url": params.bg_audio || '',
        "duration": start,
        "volume": 0.5,
        "start": 0,
        "end": start
    }];

    // Total duration timeline (output as an array of objects directly containing start and end)
    let totalTimeline = [{
        "start": 0,
        "end": totalDuration
    }];

    return {
        audios: JSON.stringify(audios),
        images: JSON.stringify(images),
        images2: JSON.stringify(images2),
        texts: JSON.stringify(texts),
        effect_infos: JSON.stringify(effect_infos),
        bg_mp3: JSON.stringify(bg_mp3),
        totalTimeline: JSON.stringify(totalTimeline), // Output as a flat array of objects
    };
}

