from typing import TypedDict, List, Dict, Any

# 定义输入参数的类型
class Args(TypedDict):
    params: Dict[str, Any]

# 定义输出结果的类型
class Output(TypedDict):
    output: str


async def main(args: Args) -> Output:
    try:
        params = args["params"]
        timeline1 = params["timeline1"]
        end2 = params["end2"]

        if not timeline1:
            raise ValueError("timeline1 is empty")

        # 提取 timeline1 中第一个字典元素的 "end" 值
        end_value = timeline1[0]["end"]

        value1 = round(end2 / end_value * 100)
        value2 = round(value1 * 0.6)

        result = f"0|{value2}|{value1}"

        ret: Output = {
            "output": result
        }
        return ret
    except KeyError as e:
        raise ValueError(f"Missing required key in input: {str(e)}") from e
    except ZeroDivisionError:
        raise ValueError("The value of end in timeline1 cannot be zero") from e
    except Exception as e:
        raise Exception(f"Error in main: {str(e)}") from e
