function splitSubtitle(text) {
    // å®šä¹‰æ ‡ç‚¹ç¬¦å·æ­£åˆ™ï¼ˆå¯è‡ªè¡Œæ‰©å±•ï¼‰
    const punctuationRegex = /[ã€‚ï¼ï¼Ÿï¼›ï¼Œã€\.!?;,]/g;

    // æŒ‰æ ‡ç‚¹åˆ†å‰²ï¼ŒåŒæ—¶æ•è·éæ ‡ç‚¹å†…å®¹ï¼ˆåŒ…æ‹¬ä¸­è‹±æ–‡æ ‡ç‚¹ï¼‰
    const segments = text.split(new RegExp(`(${punctuationRegex.source})`));

    // åˆå¹¶ç›¸é‚»éæ ‡ç‚¹å†…å®¹ï¼Œè¿‡æ»¤ç©ºå­—ç¬¦ä¸²å’Œæ ‡ç‚¹ç¬¦å·
    const result = [];
    let currentSegment = '';

    for (const segment of segments) {
      if (punctuationRegex.test(segment)) {
        if (currentSegment.trim().length > 0) {
          result.push(currentSegment.trim());
          currentSegment = '';
        }
      } else {
        currentSegment += segment;
      }
    }

    // å¤„ç†æœ€åä¸€æ®µ
    if (currentSegment.trim().length > 0) {
      result.push(currentSegment.trim());
    }

    return result;
  }

